(234, 36, 'nhr_1', 'AISI 201 — АНАЛОГ 12Х15Т9НД  AISI 304 — АНАЛОГ 08Х18Н10  AISI 410 — АНАЛОГ 12Х13  AISI 430 — АНАЛОГ 12Х17'),
(235, 36, 'nhr_2', '0,4 Х 40— 0,4 X 70 — 0,4 Х 100 И ДРУГИЕ ТИПЫ РАЗМЕРОВ'),
(236, 36, 'nhr_3', ''),
