INSERT INTO `wp_term_taxonomy` (`term_taxonomy_id`, `term_id`, `taxonomy`, `description`, `parent`, `count`) VALUES
(1, 1, 'category', '', 0, 0),
(5, 5, 'category', 'Плющенная лента используется практически повсеместно: в машиностроении, для изготовления механизмов мебели, промышленных станков и разнообразных электронных приборов. Такой широкий диапазон применения объясняется высокими качественными характеристиками изделия и его универсальностью.', 0, 3),
(6, 6, 'category', '<p>Благодаря устойчивости к влиянию воздуха, влаги, масел и других веществ, а также из-за ее качественных характеристик – термостойкости, хорошей гибкости, устойчивости к растяжениям и разрывам, низкому весу – нержавеющая лента широко распространена и очень популярна.</p>\r\n\r\n<p>В жизни изделия из нее можно встретить практически повсеместно. Это и стальные емкости для приготовления пищи, которые ежедневно используются в пищевой промышленности, и элементы металлоконструкций для строительства домов, мостов, эстакад, заграждений; это детали автомобилей, бытовой техники, инструментов и различных приборов, встречающихся на производствах.</p>', 0, 2),
(7, 7, 'category', 'Стальная лента используется для ремонта мебели, автомобилей, техники, помещений из металлоконструкций, а также для постройки домов, изготовлении труб, создания декоративных элементов. Такой широкий диапазон применения объясняется высокими качественными характеристиками изделия и его универсальностью.', 0, 4),
(8, 8, 'category', 'Стальная проволока продается оптом от производителя «Интеграл-М». Доставка товара осуществляется по Магнитогорску, а также транспортными компаниями по всей России. Высокое качество продукции подтверждено сертификатами международных стандартов и отзывами крупных предприятий, ведущих с нами плотное сотрудничество.', 0, 2),
(9, 9, 'category', 'Мюзле ТУ 25.92.99-002-51478134-2018 реализуется компанией «Интеграл-М» в Магнитогорске с доставкой по всей России и странам СНГ. Предлагаемые крупнооптовые цены применимы к заказам продукции, как крупным, так и мелким оптом. Мюзле применяется для упаковки бутылок с шампансиким и шипучими (игристыми) винами.\r\n\r\n<iframe src=\"https://www.youtube.com/embed/YqK5jbE_VZg\" width=\"940\" height=\"450\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe>', 0, 0),
(10, 10, 'nav_menu', '', 0, 19),
(11, 11, 'nav_menu', '', 0, 16),
(12, 12, 'nav_menu', '', 0, 18);
